formElements.rule:
'use strict';
({params, imports}) => {
  const programEncounter = params.entity;
  const formElement = params.formElement;
  const statusBuilder = new
  imports.rulesConfig.FormElementStatusBuilder({programEncounter, formElement});
  statusBuilder.show().when.valueInCancelEncounter('Reason Of Cancellation survey').containsAnswerConceptName("Other");
  return statusBuilder.build();
};

visitScheduleRule:
"use strict";
({params, imports}) => {
    const Encounter = params.entity;
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({Encounter});

    const reason = Encounter.findCancelEncounterObservationReadableValue("Reason Of Cancellation survey");
    const encDate=Encounter.earliestVisitDateTime;
    const earliestDate=imports.moment(encDate).add('1', 'year').toDate();
    const maxDate = imports.moment(earliestDate).add('15', 'days').toDate();
    console.log("======>Enconter",Encounter);


    if (reason=='Other' ) {
        scheduleBuilder.add({
            name: Encounter.name,
            encounterType:Encounter.encounterType.name,
            earliestDate: earliestDate,
            maxDate
        });
    }
    return scheduleBuilder.getAll();
};
